			var idActual="<?php echo $recurso->id; ?>";
			if($("#txt-oferta").val()==""){
				swal({
					title: "INGRESA TU OFERTA",
					text: "Debes ingresar un valor númerico que supere la oferta actual",
					imageUrl: "galeria/img/logos/bill-ok.png",
					showConfirmButton: true				
				});
				return false;
			}
			var valor=parseFloat($("#txt-oferta").val());
			if(valor<=ofertaActual){
				swal({
					title: "INGRESA UNA MEJOR OFERTA",
					text: "Tu oferta debe ser mayor a la actúal",
					imageUrl: "galeria/img/logos/bill-ok.png",
					showConfirmButton: true,
				});
				return false;
			}
			var valor=parseFloat($("#txt-oferta").val());
			if(valor<ofertaInicial){
				swal({
					title: "INGRESA UNA MEJOR OFERTA",
					text: "Tu oferta debe ser mayor o igual al precio inicial",
					imageUrl: "galeria/img/logos/bill-ok.png",
					showConfirmButton: true,
				});
				return false;
			}		
			swal({
				title:"Confirmación",
				text:"Confirma realizar la oferta",
				imageUrl:"galeria/img/logos/bill-ok.png",
				showConfirmButton:true,
				showCancelButton:true
			},
			function(isConfirm){
				if(isConfirm){
					limite=valor>=limiteActual?1:0;
					vendedor=$("#detalle-vendedor span:nth(1)").text();
					$.ajax({
						url:"fcn/f_index.php",
						data:{metodo:"pujar",id:idActual,limite:limite,monto:valor,vendedor:vendedor},
						type:"POST",
						dataType:"json",
						success:function(data){
							console.log(data);
							if(data.result=="ok"){
								if(limite==1){
									swal({
										title: "EL PRODUCTO ES TUYO",
										text: "Felicidades, ganastes la subasta, te llegaran los datos del vendedor por email",
										imageUrl: "galeria/img/logos/bill-ok.png",
										showConfirmButton: true,
									},
									function(){
										document.location.reload();								
									});
								}else{
									document.location.reload();
								}
							}
						},
						error:function(xhr){
							console.log(xhr);
						}
					});
				}
			});
		});
	});
</script>
</body></html>